mapper.select:
select * from insurance_order


         or id like binary"%"#{keywords}"%"
        or insured_person_name like binary"%"#{keywords}"%"
        or proposer like "%"#{keywords}"%"
        or FORMAT(goods_value,3) like binary"%"#{keywords}"%"
        or approval_date like binary"%"#{keywords}"%"
        or state_id in(select id from data_dictionary_item where data_dictionary_item.name like binary"%"#{keywords}"%")
        or department_id in(select id from data_dictionary_item where data_dictionary_item.name like binary"%"#{keywords}"%")
        or id in(select order_id from insurance_order_item where insure_company_name like binary"%"#{keywords}"%")
        or id in(select order_id from insurance_order_item where premium like binary"%"#{keywords}"%")
        or id in(select order_id from insurance_order_item where insure_date like binary"%"#{keywords}"%")
        or id in(select order_id from insurance_order_item where insure_date like binary"%"#{keywords}"%")


        and id like "%"#{id}"%"


        and insured_person_name like "%"#{insuredPersonName}"%"


        and proposer like "%"#{proposer}"%"


        and goods_value =#{goodsValue}


        and proposer like "%"#{proposer}"%"


         and DATE_FORMAT(approval_date, '%Y-%m-%d') >=  DATE_FORMAT(#{approvalDateBegin}, '%Y-%m-%d') 


         and DATE_FORMAT(approval_date, '%Y-%m-%d') <=  DATE_FORMAT(#{approvalDateEnd}, '%Y-%m-%d') 


        and yht_goods=#{yHTGoods}


        and goods_type=#{goodsType}


        and actual_carrier=#{actualCarrier}


        and file_insurance=#{fileInsurance}


        and insurance_order_requirement=#{insuranceOrderRequirement}


        and state_id=#{orderStateDataItem.id}


        and state_id!=4


        and department_id=#{departmentDataItem.id}



        limit #{page.start}, #{page.count}

select count(*) from insurance_order
or id like binary"%"#{keywords}"%" or insured_person_name like binary"%"#{keywords}"%" or proposer like "%"#{keywords}"%" or FORMAT(goods_value,3) like binary"%"#{keywords}"%" or approval_date like binary"%"#{keywords}"%" or state_id in(select id from data_dictionary_item where data_dictionary_item.name like binary"%"#{keywords}"%") or department_id in(select id from data_dictionary_item where data_dictionary_item.name like binary"%"#{keywords}"%") or id in(select order_id from insurance_order_item where insure_company_name like binary"%"#{keywords}"%") or id in(select order_id from insurance_order_item where premium like binary"%"#{keywords}"%") or id in(select order_id from insurance_order_item where insure_date like binary"%"#{keywords}"%") or id in(select order_id from insurance_order_item where insure_date like binary"%"#{keywords}"%")
and id like "%"#{id}"%"
and insured_person_name like "%"#{insuredPersonName}"%"
and proposer like "%"#{proposer}"%"
and goods_value =#{goodsValue}
and proposer like "%"#{proposer}"%"
and DATE_FORMAT(approval_date, '%Y-%m-%d') >= DATE_FORMAT(#{approvalDateBegin}, '%Y-%m-%d')
and DATE_FORMAT(approval_date, '%Y-%m-%d') <= DATE_FORMAT(#{approvalDateEnd}, '%Y-%m-%d')
and yht_goods=#{yHTGoods}
and goods_type=#{goodsType}
and actual_carrier=#{actualCarrier}
and file_insurance=#{fileInsurance}
and insurance_order_requirement=#{insuranceOrderRequirement}
and state_id=#{orderStateDataItem.id}
and state_id!=4
and department_id=#{departmentDataItem.id}

mapper.update:
insert into insurance_order_item(id, order_id, insure_company_name, insure_date, premium, creation_time, modify_time, state_id, abnormalPerson, abnormalInfo)
values
(#{newId},#{orderId},#{insureCompanyName},#{insureDate},#{premium},NOW(),NOW(),9,#{abnormalPerson},#{abnormalInfo});


        update insurance_order_item set id=#{newId},insure_company_name=#{insureCompanyName},insure_date=#{insureDate},
        premium=#{premium},modify_time=NOW(),modifier=#{modifier},order_id=#{orderId} where id=#{id};


    update goods_value set insurance_orderItem_id=#{newId},modify_time=NOW(),insurance_order_id=#{orderId} where  id=#{goodsValueId};

mapper.insert:
insert into insurance_order(id, state_id, proposer, department_id, approval_date, insured_person_name, goods_name, goods_type, package_number, package_weight, actual_carrier, original_place, receive_time, destination, flight_name, flight_number, flight_shift, truck_shift, license_plate, goods_value, yht_goods, international_freight_id, domestic_freight_id, insurance_order_requirement, file_insurance, other_insurance, order_maker_service, creation_time, modify_time, modifier, quote, invoiceUrl, boxUrl, fee_Cost_Type)
values
(#{id},"2",#{proposer},#{departmentDataItem.id},#{approvalDate},#{insuredPersonName},#{goodsName},#{goodsType},#{packageNumber},#{packageWeight},#{actualCarrier},#{originalPlace},#{receiveTime},#{destination},#{flightName},#{flightNumber},#{flightShift},#{truckShift},#{licensePlate},#{goodsValue},#{yHTGoods},#{internationalFreightDataItem.id},#{domesticFreightDataItem.id},#{insuranceOrderRequirement},#{fileInsurance},#{otherInsurance},#{orderMakerService},NOW(),NOW(),#{modifier},#{quote},#{invoiceUrl},#{boxUrl},#{feeCostType});
